return {
    payload: {
        openPercent: msg.payload.open,
    },
    topic: msg.topic,
};

if (msg.topic.startsWith('ha-switch/switch-blinds-')) {
    return {
        payload: {
            open: msg.payload.openPercent,
            online: msg.payload.online,
        },
        topic: msg.topic,
    };
}

if (typeof msg.payload !== 'boolean') {
    return;
}

const parts = msg.topic.split('/');
parts[parts.length - 1] = 'state';

return {
    payload: {
        online: msg.payload,
    },
    topic: parts.join('/'),
};

return {
    ...msg,
    topic: `${msg.topic}/set`,
};

return {
    payload: {
        on: [msg.payload],
    },
    topic: msg.topic,
};

if (msg.topic.startsWith('ha-switch/switch-onoff-')) {
    return {
        payload: {
            ...msg.payload,
            ...('on' in msg.payload
                && Array.isArray(msg.payload.on)
                && msg.payload.on.length
                ? { on: msg.payload.on[0] }
                : {}),
        },
        topic: msg.topic,
    };
}

const now = new Date();
const last = context.get(msg.topic) || {};
const span = last ? (now.getTime() - last.lastTick) / 1000 : null;
const maxSpan = last && span ? Math.max(last.maxSpan, span) : null;

context.set(msg.topic, {
    lastTick: now.getTime(),
    last: now.toLocaleString(),
    maxSpan,
});

return msg;

const now = new Date();
const status = msg.payload === 'POWER' ? 'ONLINE' : 'OFFLINE';


flow.set('grid_offline', status === 'OFFLINE');
flow.set('last_grid_status_change', new Date().getTime());

const key = `${now.getMonth() + 1}-${now.getDate()}`;
const powerLosses = flow.get('power') || {};
let perDay = powerLosses[key];
if (!perDay) {
    powerLosses[key] = perDay = [];
}
if (perDay[perDay.length - 1]?.status === status) {
    //handle restarts of nodered...
    return;
}
perDay.push({ at: now.toLocaleTimeString(), status });
flow.set('power', powerLosses);

let recovered = '';
if (status === 'OFFLINE') {
    context.set('offline', now.getTime());
} else {
    const offlineAt = context.get('offline');
    context.set('offline', null);
    if (typeof offlineAt === 'number') {
        let span = (now.getTime() - offlineAt) / 1000;
        let unit = 'sec';
        if (span >= 60) {
            span /= 60;
            unit = 'min';

            if (span >= 60) {
                span /= 60;
                unit = 'h';

                if (span >= 24) {
                    span /= 24;
                    unit = 'day';
                }
            }
        }

        recovered = ` Recovered after ${span} ${unit}.`;
    }
}


return {
    payload: {
        tag: `power`,
        title: 'Grid Status',
        body: `${status} at ${now.toLocaleString()}.${recovered}`,
    },
};

if (msg.payload.battery.soc < 25) {
    return {
        payload: {
            tag: 'ups-battery',
            title: 'Warning',
            body: `UPS battery low (${msg.payload.battery.soc}%)`,
        },
    };
}

if (msg.payload?.battery_low === true ||
    msg.payload?.battery < 20) {
    const now = new Date().toLocaleString();
    const location = msg.topic.substring(msg.topic.indexOf('/') + 1);
    return {
        topic: location,
        payload: {
            tag: location,
            body: `${location} at ${now}. Battery: ${msg.payload.battery ?? -1}. Low: ${msg.payload.battery_low}`,
        },
    };
}

const now = new Date();
return {
    payload: {
        tag: 'ups-connection',
        title: 'Warning',
        body: `UPS connection is down ${now.toLocaleString()}`,
    }
};

const { grid: { voltage, frequency }, battery: { soc } } = msg.payload;


node.status({
    fill: "blue",
    shape: "ring",
    text: `${voltage}V ${Math.round(frequency * 100) / 100}Hz ${soc}%`,
});

if (typeof msg.payload !== 'boolean' || msg.payload) {
    return;
}

const lastGridStatusChange = flow.get('last_grid_status_change');
if (typeof lastGridStatusChange === 'number') {
    const now = new Date().getTime();
    if (now - lastGridStatusChange < 300000) {
        // grid status changed in the last 5 mins, ignore offline messages
        return;
    }
}

const lastOfflines = context.get('last5min') || {};
const now = new Date().getTime();
for (const key of Object.keys(lastOfflines)) {
    const delta = (now - lastOfflines[key]) / 1000;
    if (delta > 60) {
        delete lastOfflines[key];
    }
}

const name = msg.topic.split('/')[1];
lastOfflines[name] = now;
context.set('last5min', lastOfflines);

if (flow.get('grid_offline') === false) {
    const names = Object.keys(lastOfflines).join(',');
    const nowString = new Date().toLocaleString();
    node.send({
        payload: {
            tag: `devices-offline`,
            title: 'Device(s) offline',
            body: `${names} at ${nowString}`,
        },
    });
}

const RESET_CAUSE = {
    '0': 'NO_MEAN',
    '1': 'POWERON',
    '3': 'SW',
    '4': 'OWDT',
    '5': 'DEEPSLEEP',
    '6': 'SDIO',
    '7': 'TG0WDT_SYS',
    '8': 'TG1WDT_SYS',
    '9': 'RTCWDT_SYS',
    '10': 'INTRUSION',
    '11': 'TGWDT_CPU',
    '12': 'SW_CPU',
    '13': 'RTCWDT_CPU',
    '14': 'EXT_CPU',
    '15': 'RTCWDT_BROWN_OUT',
    '16': 'RTCWDT_RTC',
};

const reason = msg.payload;
const name = msg.topic.split('/')[1];
const last = context.get('last5min') || {};
const now = new Date().getTime();

for (const key of Object.keys(last)) {
    const delta = (now - last[key].at) / 1000;
    if (delta > 60) {
        delete last[key];
    }
}

last[name] = { at: now, reason: RESET_CAUSE[reason] ?? 'unknown' };
context.set('last5min', last);

const names = Object.keys(last).map(k => `${k}-${last[k].reason}`).join(', ');
const nowString = new Date().toLocaleString();
node.send({
    payload: {
        tag: `devices-reset`,
        title: 'Device(s) reset',
        body: `${names}`,
    },
});

let name = msg.topic.substring(msg.topic.indexOf('/') + 1);

const PREFIX = '/RESULT';
if (name.endsWith(PREFIX)) {
    name = name.substring(0, name.length - PREFIX.length);
}

const previousPayload = global.get(name) ?? {};
const payload = {
    ...previousPayload,
    ...v('temperature'),
    ...v('humidity'),
    ...v('state', 'on', p => p === 'ON'),
    ...v('POWER', 'on', p => p === 'ON'),
    ...v('Dimmer', 'brightness'),
    online: msg.payload?.online ?? true,
};
global.set(name, payload);

return {
    payload,
    topic: msg.topic,
};

/**
* @param {string} input
* @param {string} output
* @param {vCallback} transform
* @returns {Record<string, *>}
*/
function v(input, output = input, transform = x => x) {
    if (input in msg.payload) {
        return {
            [output]: transform(msg.payload[input])
        };
    }
    return {};
}

