if (!msg.payload) {
    context.set('light_on', true);
    turn(true);
} else if (context.get('light_on')) {
    context.set('light_on', false);
    turn(false);
}

function turn(on) {
    node.send([[{
        payload: { on },
        topic: 'ha-switch/switch-dimmer-11/state/set',
    }, {
        payload: { on: [on] },
        topic: 'ha-switch/switch-onoff-7/state/set',
    }]]);
}

