return {
    payload: {
        state: msg.payload ? 'ON' : 'OFF',
    },
    topic: msg.topic + '/set',
};

if (!msg.payload) {
    global.set('dark', true);
    const skip = flow.get('skip_night_light');
    context.set('light_on', !skip);
    if (!skip) {
        const state = global.get('light/outside');
        if (!state?.on) {
            return [{
                topic: 'zigbee2mqtt/light/outside/set',
                payload: { state: 'ON' },
            }, {
                //send notification
            }];
        }
    }
} else if (context.get('light_on')) {
    global.set('dark', false);
    context.set('light_on', false);
    return [{
        topic: 'zigbee2mqtt/light/outside/set',
        payload: { state: 'OFF' },
    }, null, {
        //set skip to false
        payload: false,
    }];
}

const start = msg.topic.indexOf('/') + 1;
const deviceName = msg.topic.substr(start, msg.topic.lastIndexOf('/') - start);
const state = global.get(deviceName);

const messages = [{
    payload: msg.payload.on ? 'ON' : 'OFF',
    topic: `cmnd/${deviceName}/Power`,
}];

if ('brightness' in msg.payload &&
    msg.payload.brightness !== state?.brightness) {
    messages.push({
        payload: msg.payload.brightness,
        topic: `cmnd/${deviceName}/Dimmer`,
    });
}

return [messages];

if (msg.payload.action === 'single') {
    return {
        payload: {
            state: 'TOGGLE',
        },
        topic: 'zigbee2mqtt/light/hallway/set',
    };
}

if (msg.payload === true) {
    return {
        topic: 'zigbee2mqtt/light/outside/set',
        payload: { state: 'OFF' },
    };
}

const location = msg.topic.substr(msg.topic.lastIndexOf('/') + 1);
context.set(location, msg.payload.occupancy);

if (!flow.get('away_mode')) {
    return;
}

if (msg.payload.occupancy) {
    const now = new Date().toLocaleString();
    return {
        payload: {
            title: 'Motion Detected',
            body: `In ${location}, at: ${now}`,
        },
    };
}

const now = new Date();

if (msg.payload.occupancy &&
    global.get('dark') &&
    flow.get('auto_light') &&
    !global.get('light/hallway')?.on) {
    return {
        payload: { state: 'ON' },
        topic: 'zigbee2mqtt/light/hallway/set',
    };
}

if (msg.payload.state === 'OFF') {
    return { payload: 'clear' };
}

if (flow.get('away_mode')) {
    return [{
        //office lamp
        payload: 'ON',
        topic: 'cmnd/outlet/t2/Power',
    }, {
        //wake light
        payload: {
            state: 'ON',
        },
        topic: 'zigbee2mqtt/relay/bedroom_wake/set',
    }];
}

const prev = context.get('prev');
const powerOn = msg.payload.POWER === 'ON';
context.set('prev', powerOn);

if (powerOn && powerOn !== prev) {
    let topic = msg.topic.split('/');
    topic = topic.slice(1, topic.length - 1);
    setTimeout(() => {
        node.send({
            payload: 'off',
            topic: ['cmnd', ...topic, 'power'].join('/'),
        });
    }, 17000);
}

if (flow.get('auto-feed-dog')) {
    return msg;
}
