function getIntegral(
  /** @type {{
   * measurement: 'solar'|'out-active'|'grid', 
   * range: 'today'|'week'|'month'
   * }} */
  { measurement, range }
) {
  const now = new Date();
  now.setHours(0, 0, 0, 0);

  let start;

  if (range === 'today') {
    start = now.toISOString()
  } else if (range === 'week') {
    const day = now.getDay();
    const diff = now.getDate() - day + (day == 0 ? -6 : 1); // adjust when day is sunday
    now.setDate(diff);
    if (now < TURN_ON_TIME) {
      start = TURN_ON_TIME.toISOString();
    } else {
      start = now.toISOString()
    }
  } else if (range === 'month') {
    now.setDate(1);
    if (now < TURN_ON_TIME) {
      start = TURN_ON_TIME.toISOString();
    } else {
      start = now.toISOString()
    }
  }

  let q;
  switch (measurement) {
    case 'solar': q = "solar-input-power"; break;
    case 'out-active': q = "ac-output-active-power"; break;
    case 'grid': q = "ac-input-active-power"; break;
  }


  node.send({
    query: `from(bucket: "power")
  |> range(start: ${start}, stop: 1d )
  |> filter(fn: (r) => r["_measurement"] == "${q}")
  |> filter(fn: (r) => r["_field"] == "total")
  |> integral(unit: 1h, column: "_value")`,
    topic: `${measurement}-${range}`,
  });
}

