    return;
}

const p = msg.payload;
if (found.turn === 'on') {
    let turnOn = false;

    if ('occupancy' in p) {
        const last = context.get(`last-${name}`);
        if (p.occupancy || last) {
            turnOn = true;
        }
        context.set(`last-${name}`, p.occupancy);
    }

    if ('contact' in p) {
        const last = context.get(`last-${name}`);
        if (p.contact !== last) {
            turnOn = true;
        }
        context.set(`last-${name}`, p.contact);
    }

    if (turnOn) {
        return {
            payload: {
                'brightness': 100,
                'for': found.timeoutMin * 60,
            },
            topic: `ha-switch/${found.target}/state/set`,
            retain: false,
        };
    }
} else if (found.turn === 'off') {
    if (p.occupancy) {
        return [, {
            payload: {
                on: false
            },
            delay: found.timeoutMin * 60 * 1000,
            topic: `ha-switch/${found.target}/state/set`,
            retain: true,
        }];
    }
}

const valueName = msg.topic.split('/').slice(-1)[0];
if (typeof msg.payload.occupancy !== 'boolean') {
    return;
}

return {
    payload: { [valueName]: msg.payload.occupancy ? 1 : 0 },
    measurement: 'occupancy',
};

const valueName = msg.topic.split('/').slice(-1)[0];
if (typeof msg.payload.contact !== 'boolean') {
    return;
}

return {
    payload: { [valueName]: msg.payload.contact ? 1 : 0 },
    measurement: 'contact',
};

return {
    ...msg,
    topic: 'dark',
}

if (!global.get('awayMode') && 
    !msg.payload && 
    global.get('batteryCapacity') > 96) {
    return {
        payload: 1,
        topic: 'more-hot-water',
    };
}

const now = new Date();
const isWorkingDay = now.getDay() >= 1 && now.getDay() <= 5;
const isWorkingHour = now.getHours() >= 9 && now.getHours() <= 20;

// percentage of an hour that should be on
const timeSpentOn = isWorkingDay && isWorkingHour
    ? .15
    : .05;

const isAway = !!global.get('awayMode');
const dutyOn = now.getMinutes() <= timeSpentOn * 60;
const shouldBeOn = !isAway && dutyOn;
const value = shouldBeOn ? 100 : 0;

const lastValue = context.get('lastValue');
if (lastValue === value) {
    return;
}

context.set('lastValue', value);
node.status({
    fill: 'blue',
    text: `${shouldBeOn ? 'on' : 'off'} - ${timeSpentOn * 100}% duty`
});

return { payload: { value } };

const start = msg.topic.indexOf('/') + 1;
const deviceName = msg.topic.substring(start, msg.topic.lastIndexOf('/'));

let state = flow.get(deviceName) ?? {};
state = {
    ...state,
    ...('POWER' in msg.payload ? { on: msg.payload.POWER == 'ON' } : {}),
    ...('Dimmer' in msg.payload ? { brightness: msg.payload.Dimmer } : {}),
    online: 'online' in msg.payload ? msg.payload.online : true,
};
flow.set(deviceName, state);

return {
    payload: state,
    topic: deviceName,
};

const deviceName = msg.topic;
const state = flow.get(deviceName);

const messages = [{
    payload: msg.payload.on ? 'ON' : 'OFF',
    topic: `cmnd/${deviceName}/Power`,
}];

if (msg.payload.brightness !== state?.brightness) {
    messages.push({
        payload: msg.payload.brightness,
        topic: `cmnd/${deviceName}/Dimmer`,
    });
}

return [messages];

const keys = flow.keys();

const queryTopics = [];

for (const key of keys) {
    const state = flow.get(key);

    if (typeof state !== 'object') {
        continue;
    }

    if ('on' in state) {
        queryTopics.push(`cmnd/${key}/Power`);
    }

    if ('brightness' in state || key.indexOf('dimmer') >= 0) {
        queryTopics.push(`cmnd/${key}/Dimmer`);
    }
}

if (queryTopics.length) {
    return [queryTopics.map(q => ({
        payload: '',
        topic: q,
    }))];
}
