const humidity = msg.payload.value;
if (humidity < 0 || humidity > 100) {
    return;
}

const stopRecovery = flow.get('bypass_recovery') === 1;
const target = 45;
const histerezis = 2;
const compareTo = target + (stopRecovery ? -1 : 1) * histerezis;
const shouldStopRecovery = humidity >= compareTo;

node.status({
    fill: shouldStopRecovery ? 'red' : "blue",
    shape: "dot",
    text: `recovery ${shouldStopRecovery ? 'off' : 'on'}`,
});

return {
    payload: shouldStopRecovery ? 1 : 0,
};

return {
    payload: msg.payload.value,
    topic: msg.topic,
};

if (msg.payload.value < -300) {
    return;
}

const parts = msg.topic.split('/');
const rooms = parts.length === 2
    ? [parts[0]]
    : (context.keys() ?? []);
const prop = parts[parts.length - 1];

const msgs = rooms.map(room => {
    const state = {
        ...(context.get(room) ?? {}),
        mode: 'heatcool',
        activeMode: 'cool',
    };
    state[prop] = msg.payload.value;
    context.set(room, state);
    return { topic: room, payload: state };
});

return [msgs];

const propsToCopy = ['setpointLow', 'setpointHigh'];
const messages = propsToCopy.map(key => ({
    topic: `${msg.topic}/${key}`,
    payload: msg.payload[key],
}));
return [messages]

return {
    ...msg,
    topic: 'online',
}

if (msg.payload.value < -300) {
    return;
}

const parts = msg.topic.split('/');
const measurement = parts[0];
const payload = parts.length === 1
    ? msg.payload.value
    : { [parts[1]]: msg.payload.value }

return {
    payload,
    measurement,
};

if (msg.payload.value < -300) {
    return;
}

const [measurement, valueName] = msg.topic.split('/');
return {
    payload: { [valueName]: msg.payload.value },
    measurement,
};

const map = {
    '10': 'off',
    '20': 'hotwater',
    '30': 'heat',
    '40': 'pool',
    '60': 'cooling',
};

node.status({ fill: "blue", shape: "dot", text: map[msg.payload.value] });
return msg;

node.status({ fill: "blue", text: `${msg.payload.value} - ${status()}` });

return {
    payload: msg.payload.value === 1 ? 1 : 0,
    topic: msg.topic,
};

function status() {
    switch (msg.payload.value) {
        case 0: return 'normal';
        case 1: return 'off';
        case 2: return 'low';
        case 3: return 'high';
        case 4: return 'boost';
    }
}

const oldValue = flow.get('bypass_recovery');
const newValue = msg.payload.value;
if (newValue !== oldValue) {
    flow.set('bypass_recovery', newValue);
}

return {
    payload: {
        openPercent: msg.payload.open,
    },
    topic: msg.topic,
};

if (msg.topic.startsWith('ha-switch/switch-blinds-')) {
    return {
        payload: {
            open: msg.payload.openPercent,
            online: msg.payload.online,
        },
        topic: msg.topic,
    };
}

if (typeof msg.payload !== 'boolean') {
    return;
}

const parts = msg.topic.split('/');
parts[parts.length - 1] = 'state';

return {
    payload: {
        online: msg.payload,
    },
    topic: parts.join('/'),
};

return {
    retain: true,
    ...msg,
    topic: `${msg.topic}/set`
};

const m = /([^.]+)\.(\d+)/.exec(msg.topic);
const topic = m ? m[1] : msg.topic;
const index = m ? +m[2] : 0;

const on = [];
on[index] = msg.payload;

return {
    payload: {
        on,
    },
    topic,
};

if (msg.topic.startsWith('ha-switch/switch-onoff-')) {

    if (Array.isArray(msg.payload.on) && msg.payload.on.length > 1) {
        //multiple states on switch
        return [msg.payload.on.map((on, index) => ({
            payload: {
                ...msg.payload,
                on,
            },
            topic: `${msg.topic}.${index}`
        }))];
    } else {
        return {
            payload: {
                ...msg.payload,
                on: msg.payload?.on?.[0] ?? msg.payload?.on,
            },
            topic: msg.topic,
        };
    }
}

/**
 * @type Map<string, {topic:string, type:'onoff'|'offonly'}>
 */
const CTRL = new Map([
    ['ha-switch/switch-dimmer-5/state', { topic: 'ha-switch/switch-onoff-4/state', type: 'onoff' }],
    ['ha-switch/switch-dimmer-9/state', { topic: 'ha-switch/switch-onoff-6/state', type: 'onoff' }],
    ['ha-switch/switch-onoff-5/state', { topic: 'ha-switch/switch-onoff-8/state', type: 'offonly' }],
]);

const topic = msg.topic;
const to = CTRL.get(topic);
if (!to) {
    return;
}

let on = msg.payload.on;
if (Array.isArray(on) && on.length) {
    on = on[0];
}
if (typeof on != 'boolean') {
    return;
}

const lastOn = context.get(topic);
if (lastOn !== on) {
    context.set(topic, on);

    if (to.type === 'offonly' && on) {
        return;
    }

    return {
        topic: to.topic,
        payload: {
            on: [on],
        },
    };
}

const value = global.get(msg.topic) || { online: true };

if (Array.isArray(msg.payload.on) && msg.payload.on.length === 1) {
    msg.payload.on = msg.payload.on[0];
}

global.set(msg.topic, {
    ...value,
    ...msg.payload,
});

return msg;

const regex = /^ha-switch\/switch-.+-gate\/state$/
if (regex.test(msg.topic)) {
    return {
        payload: {
            online: msg.payload.online,
        },
        topic: msg.topic,
    };
}

if (msg.payload) {
    return {
        payload: {
            on: [true],
            for: 2
        },
        topic: msg.topic,
        retain: false,
    };
}

return [[{
    payload: msg.payload.acInput.voltage,
    measurement: 'ac-input-voltage',
}, {
    payload: msg.payload.acInput.frequency,
    measurement: 'ac-input-freq',
}, {
    payload: msg.payload.acOutput.voltage,
    measurement: 'ac-output-voltage',
}, {
    payload: msg.payload.acOutput.frequency,
    measurement: 'ac-output-freq',
}, {
    payload: {
        voltage: msg.payload.battery.voltage,
        current: msg.payload.battery.current,
        capacity: msg.payload.battery.capacity,
    },
    measurement: 'battery',
}]];

return [[{
    payload: msg.payload.acInput.activePower,
    measurement: 'ac-input-active-power',
}, {
    payload: msg.payload.acOutput.activePower,
    measurement: 'ac-output-active-power',
}, {
    payload: msg.payload.acOutput.apparentPower,
    measurement: 'ac-output-apparent-power',
}]];

const lastPowerStatus = context.get('power_status');
const powerStatus = msg.payload.acInput.frequency > 45;

if (powerStatus === lastPowerStatus) {
    return;
}

context.set('power_status', powerStatus);

node.status({
    fill: powerStatus ? 'blue' : 'red',
    text: powerStatus ? 'online' : 'offline',
});

let changes = context.get('changes');
const today = new Date().toLocaleDateString();
const key = powerStatus ? 'recovered' : 'lost';
if (changes?.key === today) {
    changes[key]++;
} else {
    changes = {
        key: today,
        recovered: 0,
        lost: 0,
        [key]: 1,
    };
}
let timeToRecover = '';
if (!powerStatus) {
    context.set('lostAt', new Date().getTime());
} else {
    const lostAt = context.get('lostAt');
    if (typeof lostAt === 'number') {
        let time = (new Date().getTime() - lostAt) / 1000;
        let unit = 'sec';
        if (time > 60) {
            time /= 60;
            unit = 'min';
        }
        if (time > 60) {
            time /= 60;
            unit = 'hour';
        }
        timeToRecover = `Recovered after ${time.toFixed(1)} ${unit}.`
    }
}
context.set('changes', changes);

const timestamp = new Date().toLocaleString();
const body = `Power ${key} at ${timestamp}, (lost: ${changes.lost}, recovered: ${changes.recovered})\n${timeToRecover}`;

return {
    payload: {
        tag: 'power-status',
        title: 'Power Status',
        body: body,
        silent: true,
    },
};

const capacity = msg.payload;
const notifyAt = [10, 25, 30, 50, 80];
const lastNotifyAt = context.get('notify_at');

if (flow.get('batteryStatus') === 'charge') {
    if (lastNotifyAt) {
        context.set('notify_at', null)
    }
    return;
}

let i;
for (i = 0; i < notifyAt.length; i++) {
    const step = notifyAt[i];
    let threshold = step;
    if (lastNotifyAt === step) {
        threshold += 5;
    }
    if (capacity <= threshold) {
        if (lastNotifyAt !== step) {
            context.set('notify_at', step)

            const timestamp = new Date().toLocaleString();
            const body = `Battery capacity ${capacity}%, at ${timestamp}`;

            return {
                payload: {
                    tag: 'battery-capacity',
                    title: 'Battery capacity',
                    body: body,
                },
            };
        }

        break;
    }
}

const status = msg.payload ? 'restored' : 'lost';
const at = new Date().toLocaleString();
let afterText = '';
if (!msg.payload) {
    context.set('lost_at', new Date().getTime());
} else {
    let after = new Date().getTime() - context.get('lost_at');
    let unit = 'sec';
    after /= 1000;
    if (after > 60) {
        after /= 60;
        unit = 'min';
    }
    if (after > 60) {
        after /= 60;
        unit = 'hour';
    }
    afterText = `, after ${after.toFixed(1)} ${unit}`
}

return {
    payload: {
        tag: "inverter-connection",
        title: "Inverter connection",
        body: `Inverter connection ${status} at ${at}${afterText}`,
    }
};

const capacity = msg.payload.battery.capacity;
const values = this.past_values ?? [];
values.splice(900);
values.splice(0, 0, capacity);
this.past_values = values;

const average = values.reduce((a, b) => a + b, 0) / values.length;
node.status({ fill: "blue", shape: "ring", text: `${average.toFixed(2)}%` });

return { payload: average };

const lastValue = flow.get('batteryStatus');
const newValue = msg.payload.status.batteryPowerDirectionStatus;
if (newValue !== lastValue) {
    flow.set('batteryStatus', newValue);
}

const now = new Date();
const last = context.get(msg.topic) || {};
const span = last ? (now.getTime() - last.lastTick) / 1000 : null;
const maxSpan = last && span ? Math.max(last.maxSpan, span) : null;

context.set(msg.topic, {
    lastTick: now.getTime(),
    last: now.toLocaleString(),
    maxSpan,
});

return msg;

if (msg.payload?.battery_low === true ||
    msg.payload?.battery < 20) {
    const now = new Date().toLocaleString();
    const location = msg.topic.substring(msg.topic.indexOf('/') + 1);
    return {
        topic: location,
        payload: {
            tag: location,
            title: 'Battery low',
            body: `${location} at ${now}. Battery: ${msg.payload.battery ?? -1}. Low: ${msg.payload.battery_low}`,

            //zigbee logo
            icon: 'data:image/png;base64,'
        },
    };
}

const RESET_CAUSE = {
    '0': 'NO_MEAN',
    '1': 'POWERON',
    '3': 'SW',
    '4': 'OWDT',
    '5': 'DEEPSLEEP',
    '6': 'SDIO',
    '7': 'TG0WDT_SYS',
    '8': 'TG1WDT_SYS',
    '9': 'RTCWDT_SYS',
    '10': 'INTRUSION',
    '11': 'TGWDT_CPU',
    '12': 'SW_CPU',
    '13': 'RTCWDT_CPU',
    '14': 'EXT_CPU',
    '15': 'RTCWDT_BROWN_OUT',
    '16': 'RTCWDT_RTC',
};

const reason = msg.payload;
const name = msg.topic.split('/')[1];
const last = context.get('last5min') || {};
const now = new Date().getTime();

for (const key of Object.keys(last)) {
    const delta = (now - last[key].at) / 1000;
    if (delta > 60) {
        delete last[key];
    }
}

last[name] = { at: now, reason: RESET_CAUSE[reason] ?? 'unknown' };
context.set('last5min', last);

const names = Object.keys(last).map(k => `${k}-${last[k].reason}`).join(', ');
const nowString = new Date().toLocaleString();
node.send({
    payload: {
        tag: `devices-reset`,
        title: 'Device(s) reset',
        body: `${names}`,
        silent: true,
    },
});

const WAIT_SEC = 20;
const ON = {
    payload: { on: true },
    topic: 'ha-switch/switch-onoff-r1/state/set',
};
const OFF = {
    payload: { on: false },
    topic: ON.topic,
};

if (msg.payload === 'cancel') {
    this.timer = null;
    clearTimeout(this.timer);
    return;
}

if (msg.payload === 'start-water') {
    node.send([, ON]);
    return;
}

if (!msg.payload.water_leak || this.timer) {
    return;
}

const time = new Date().toLocaleString();
const parts = msg.topic.split('/');
const location = parts[parts.length - 1];

const base = {
    tag: `water-leak`,
    title: 'Water Leak',
    body: `Water leak detected at ${time} - ${location}.`,
};

this.timer = setTimeout(() => {
    this.timer = null;
    node.send([{
        payload: {
            ...base,
            body: `${base.body} Stopped the water.`,
            actions: [{
                title: 'Start',
                action: 'start-water'
            }],
        },
    }, OFF]);
}, WAIT_SEC * 1000);

return {
    payload: {
        ...base,
        body: `${base.body} Stopping the water in ${WAIT_SEC} sec.`,
        actions: [{
            title: 'Cancel',
            action: 'cancel'
        }],
    },
};

const ctx = context.get(msg.topic) || {};

if (msg.payload.occupancy != ctx.occupancy) {
    ctx.occupancy = msg.payload.occupancy;
    ctx.changes = (ctx.changes ?? 0) + 1;
}

ctx.last = new Date().toString();
context.set(msg.topic, ctx);

return msg;

const valueName = msg.topic.split('/').slice(-1)[0];
if (typeof msg.payload.water_leak !== 'boolean') {
    return;
}

return {
    payload: { [valueName]: msg.payload.water_leak ? 1 : 0 },
    measurement: 'water-leak',
};

let name = msg.topic.substring(msg.topic.indexOf('/') + 1);

const PREFIX = '/RESULT';
if (name.endsWith(PREFIX)) {
    name = name.substring(0, name.length - PREFIX.length);
}

const previousPayload = global.get(name) ?? {};
const payload = {
    ...previousPayload,
    ...v('temperature'),
    ...v('humidity'),
    ...v('state', 'on', p => p === 'ON'),
    ...v('POWER', 'on', p => p === 'ON'),
    ...v('Dimmer', 'brightness'),
    ...v('occupancy'),
    online: msg.payload?.online ?? true,
};
global.set(name, payload);

return {
    payload,
    topic: msg.topic,
};

/**
* @param {string} input
* @param {string} output
* @param {(a:any)=>any} transform
* @returns {Record<string, *>}
*/
function v(input, output = input, transform = x => x) {
    if (input in msg.payload) {
        return {
            [output]: transform(msg.payload[input])
        };
    }
    return {};
}

return {
    payload: {
        state: msg.payload ? 'ON' : 'OFF',
    },
    topic: msg.topic + '/set',
};

if (!flow.get('auto_light')) {
    return;
}

/**
 * @type Array<{
 *  src:Array<string>, 
 *  target:string, 
 *  timeoutMin: number, 
 *  turn:'on'|'off'
 * }>
 */
const targetByPir = [
    {
        src: ['pir/hallway-1', 'pir/hallway-2'],
        target: 'switch-dimmer-3',
        timeoutMin: 1,
        turn: 'on',
    }, {
        src: ['pir/hallway-3', 'contact/entrance'],
        target: 'switch-dimmer-10',
        timeoutMin: 3,
        turn: 'on',
    }, {
        src: ['pir/small-bathroom'],
        target: 'switch-onoff-3',
        timeoutMin: 15,
        turn: 'off',
    }
]

const name = msg.topic.split('/').slice(1).join('/');
const found = targetByPir.find(t => t.src.includes(name));
if (!found) {
    return;
}

const p = msg.payload;
if (found.turn === 'on') {
    if (p.occupancy || p.contact === false) {
        return {
            payload: {
                'brightness': 100,
                'for': found.timeoutMin * 60,
            },
            topic: `ha-switch/${found.target}/state/set`,
        };
    }
} else if (found.turn === 'off') {
    if (p.occupancy) {
        return [, {
            payload: {
                on: false
            },
            delay: found.timeoutMin * 60 * 1000,
            topic: `ha-switch/${found.target}/state/set`,
        }];
    }
}

const valueName = msg.topic.split('/').slice(-1)[0];
if (typeof msg.payload.occupancy !== 'boolean') {
    return;
}

return {
    payload: { [valueName]: msg.payload.occupancy ? 1 : 0 },
    measurement: 'occupancy',
};

const valueName = msg.topic.split('/').slice(-1)[0];
if (typeof msg.payload.contact !== 'boolean') {
    return;
}

return {
    payload: { [valueName]: msg.payload.contact ? 1 : 0 },
    measurement: 'contact',
};

if (!msg.payload) {
    context.set('light_on', true);
    turn(true);
} else if (context.get('light_on')) {
    context.set('light_on', false);
    turn(false);
}

/**
 * @param {boolean} on
 */
function turn(on) {
    node.send([[
        {
            payload: { on },
            topic: 'ha-switch/switch-dimmer-11/state/set',
            retain: true,
        },
        {
            payload: { on: [on] },
            topic: 'ha-switch/switch-onoff-7/state/set',
            retain: true,
        },
        {
            topic: 'zigbee2mqtt-garage/light/outside/set',
            payload: { state: on ? 'ON' : 'OFF' },
        },
        ...getDeckMessages(on),
    ]]);
}

function getDeckMessages(on) {
    const deckBase = 'cmnd/tasmota_0F10CB/';
    const deckDimmer = `${deckBase}/Dimmer`;
    const deckPower = `${deckBase}/Power`;
    return on
        ? [{
            topic: deckPower,
            payload: 'ON',
        }, {
            topic: deckDimmer,
            payload: 15,
        }]
        : [{
            topic: deckPower,
            payload: 'OFF',
        }];
}

/** @type Array<{id: number, open?:number, close?: number, openLater?: number}> */
const BLINDS = [
    { id: 1, open: 15, openLater: 100, close: 0 }, // guest
    { id: 2, open: 100, close: 0 }, // kitchen-window
    { id: 8, open: 15, close: 0 }, // kitchen-fixed
    { id: 3, /*open: 15, openLater: 100,*/ close: 0 }, // big bedroom
    { id: 4, open: 100 }, // living-door
    { id: 6, open: 15, openLater: 40, close: 0 }, // living-window
    { id: 5, open: 100, close: 0 }, // office
];

const KEY = 'blindsLastCommand';

/** @type {'open'|'close'|'openLater'} */

if (!msg.payload) {
    blinds('close');
} else {
    const now = new Date();
    const minTimeToOpenBlinds = new Date();
    minTimeToOpenBlinds.setHours(7, 30, 0);

    const openLaterTime = new Date();
    openLaterTime.setHours(8, 30, 0);

    if (now >= minTimeToOpenBlinds && now < openLaterTime) {
        blinds('open');
    } else if (now >= openLaterTime) {
        blinds('openLater');
    }
}

/** @param {'open'|'close'|'openLater'} type */
function blinds(type) {
    const last = context.get(KEY);
    if (type === last) {
        return;
    }
    context.set(KEY, type);

    if (type.startsWith('open') &&
        !flow.get('autoOpenBlinds')) {
        return;
    }

    const msgs = BLINDS
        .map(b => {
            const stateTopic = `ha-switch/switch-blinds-${b.id}/state`;
            const currentState = global.get(stateTopic);

            const openPercent = b[type];
            if (typeof openPercent !== 'number') {
                return;
            }

            if (currentState) {
                switch (type) {
                    case 'open':
                    case 'openLater':
                        if (openPercent <= currentState.openPercent) {
                            return;
                        }
                        break;
                    case 'close':
                        if (openPercent >= currentState.openPercent) {
                            //blinds are already more closed
                            return;
                        }
                        break;
                }
            }

            return {
                payload: { openPercent },
                topic: `${stateTopic}/set`,
                retain: true,
            };
        })
        .filter(m => !!m);
    node.send([msgs]);
}

const start = msg.topic.indexOf('/') + 1;
const deviceName = msg.topic.substring(start, msg.topic.lastIndexOf('/'));

let state = flow.get(deviceName) ?? {};
state = {
    ...state,
    ...('POWER' in msg.payload ? { on: msg.payload.POWER == 'ON' } : {}),
    ...('Dimmer' in msg.payload ? { brightness: msg.payload.Dimmer } : {}),
    online: 'online' in msg.payload ? msg.payload.online : true,
};
flow.set(deviceName, state);

return {
    payload: state,
    topic: deviceName,
};

const deviceName = msg.topic;
const state = flow.get(deviceName);

const messages = [{
    payload: msg.payload.on ? 'ON' : 'OFF',
    topic: `cmnd/${deviceName}/Power`,
}];

if (msg.payload.brightness !== state?.brightness) {
    messages.push({
        payload: msg.payload.brightness,
        topic: `cmnd/${deviceName}/Dimmer`,
    });
}

return [messages];

const prev = context.get('prev');
const powerOn = msg.payload.POWER === 'ON';
context.set('prev', powerOn);

if (powerOn && powerOn !== prev) {
    let topic = msg.topic.split('/');
    topic = topic.slice(1, topic.length - 1);
    setTimeout(() => {
        node.send({
            payload: 'off',
            topic: ['cmnd', ...topic, 'power'].join('/'),
        });
    }, 17000);
}

if (flow.get('auto-feed-dog')) {
    return msg;
}
